import React, { useState, useEffect } from 'react';
import axios from 'axios'; // allow us to make request ourselves

export default () => {
    const [posts, setPosts] = useState({});//initial value: object

    const fetchPosts = async () => {
        const res = await axios.get('http://posts.com/posts');//return response object
        // const res = await axios.get('http://localhost:4002/posts');//return response object

        setPosts(res.data);//data is res's property

    }

    //useEffect: used to run some code at some specific point of time at a lifecycle of a component
    //only call fetchPosts when the component is firstly displayed on the screen
    useEffect(() => {
        fetchPosts();
    }, []); // [] tells react to only run this function one time

    //display all posts
    //Object.values(posts) => gives back all valus inside object posts
    //map: iterate over elements of the array and store new array in the variable
    const renderedPosts = Object.values(posts).map(post => {
        return (//jsx; react requires to set key
            <div className="" style={{width: "100%", marginBottom: "50px"}} key={post.id}>
                <div className="card-body">
                    <h3>{post.title}</h3>
                </div>
            </div>
        );
    }) //give all the elements into an array

    //display renderedPosts 
    return (
        <div className="d-flex flex-row flex-wrap justify-content-between">
            {renderedPosts}
        </div>
    );

};
