import React, {useState} from 'react';
import axios from 'axios';
import searchResultList from "./SearchResultList";

//create export functional component
const SearchCreate = () => {
    const [title, setTitle] = useState('');
    const [result, setResult] = useState('');

    const onSubmit = async (event) => {
        //prevent default submit
        event.preventDefault();

        const res = await axios.post("http://localhost:8081/search?query={title}&size=5");
        setResult(res.data)

        //ux
        //after submit, empty out input
        setTitle("");
    }

    return (
        <div>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label></label>
                    <input
                    value={title}
                    placeholder={"Type what you want to search"}
                    onChange={e => setTitle(e.target.value)}
                    className="form-control"
                    />
                    <button className="btn btn-primary">search</button>
                </div>
            </form>
            {searchResultList(result)}
        </div>
    );
};

export default SearchCreate;
