import axios from 'axios';
import React, { useState } from 'react';

//destruct props into post id
export default ({ postId }) => {
    const [content, setContent] = useState('');

    const Submit = async (event) => {
        event.preventDefault();

        // const response = await axios.post(`http://localhost:4001/posts/${postId}/comments`, { content });
        const response = await axios.post(`http://posts.com/posts/${postId}/comments`, { content });

        console.log(response);

        setContent("");
    };

    return (
        <div>
            <form onSubmit={Submit}>
                <div className='form-group'>
                    <label>New Comment</label>
                    <input 
                    className='form-control' 
                    value = {content} //this value of the input will be cleared once content is cleared
                    onChange = { e => setContent(e.target.value)} />
                </div>
                <button className='btn btn-primary'>Submit</button>
            </form>
        </div>
    );


};
